(5717, 1062, '_accordion_2_description', 'field_574592c326446'),
(5718, 1062, 'accordion_3_title', 'Doradzimy jak przechowywać produkt'),
(5719, 1062, '_accordion_3_title', 'field_574592bf26445'),
(5720, 1062, 'accordion_3_description', 'Właściwości każdego produktu mogą być utrzymane, jeżeli jest on przechowywany w odpowiedni sposób. Jeżeli mają Państwo jakiekolwiek pytania dotyczące sposobu obchodzenia się z naszym produktem, prosimy o kontakt.'),
(5721, 1062, '_accordion_3_description', 'field_574592c326446'),
