(11037, 1321, '_content_2_inwestorzy_0_name', 'field_574be92ebc074'),
(11038, 1321, 'content_2_inwestorzy_0_description', 'posiada prawie 30-letnie doświadczenie w branży suplementów diety. Jest założycielem jednej z największych firm na rynku skandynawskim sprzedających suplementy diety, w szczególności kwasy tłuszczowe omega‐3.'),
(11039, 1321, '_content_2_inwestorzy_0_description', 'field_574be933bc075'),
(11040, 1321, 'content_2_inwestorzy_1_image', '1015'),
